import React from 'react';
import ReactDOM from 'react-dom';
import arrowBack from './img/arrow_back-black-18dp.svg';
import arrowForward from './img/arrow_forward-black-18dp.svg';
import './index.css';


class Home extends React.Component {
   constructor(){
       super();
       this.state = {
           pictures: [],
           currentIndex: 0,
        };
   }
   componentDidMount() {
        fetch('/images')
            .then(res => {
                console.log(res);
                return res.json()
             })
            .then(images => { 
                console.log(images);
                this.setState({ pictures: images, currentIndex: 0 })
             });
    }

    onClickNext() {
        const nextIndex = currentIndex + 1;
        if(nextIndex === this.state.pictures.length) {
            this.setState({ 
                pictures: this.state.pictures,
                currentIndex: 0 })    
        } else {
            this.setState({
                pictures: this.state.pictures,
                currentIndex: nextIndex 
            })
        }
    }

    onClickPrevious() {
        const previousIndex = currentIndex - 1;
        if(previousIndex < 0) {
            this.setState({ 
                pictures: this.state.pictures,
                currentIndex: this.state.pictures.length - 1 })    
        } else {
            this.setState({
                pictures: this.state.pictures,
                currentIndex: previousIndex
            })
        }
    }

    render() {
        return (
            // <div className="App">
            //     <h1>Users</h1>
            //     {this.state.users.map(user =>
            //     <div key={user.id}>user: {user.name} Password: {user.password}</div>
            //   )}
            // </div>
            //
            <div className="main">
                <div className="navigation-buttons">
                  <img src={arrowBack} className="backButton" id="backButton" onClick={this.onClickPrevious}></img>
                  <img src={arrowForward} className="nextButton" id="nextButton" onClick={this.onClickNext}></img>
                </div>

                <div><p>current Index: {currentIndex}</p></div>

                <div className="image-wrapper">
                  <figure className="top-figure">
                    <figcaption>
                      <h1 id="topOut"></h1>
                    </figcaption>
                    <div id="slideShowImages">
                      <div className="imageNumber"></div>
                      <p>Loading, please wait...</p>
                    </div>
                    <figcaption>
                      <h2 id="bottomOut"></h2>
                    </figcaption>
                  </figure> 
                </div>

            </div>
        );
    }
}

// ========================================

ReactDOM.render(
  <Home />,
  document.getElementById('root')
);
