import React from 'react';
import ReactDOM from 'react-dom';
import arrowBack from './img/arrow_back-black-18dp.svg';
import arrowForward from './img/arrow_forward-black-18dp.svg';
import './index.css';


class InputSection extends React.Component {
    render() {
        return (
            <div class="inputs-wrapper"> 
                  <h2 class="form-header">Write top and bottom caption</h2>
                  <form id="topForm">
                    <input type="text" placeholder="top caption" name="top"/>
                    <button type="submit">Submit</button>
                  </form>    

                  <form id="bottomForm">
                    <input type="text" placeholder="bootom caption" name="bottom"/>
                    <button type="submit">Submit</button>
                  </form>

                  <h2 class="form-header">Post a new picture by existing url</h2>
                  <form id="postForm" onsubmit="submitForm(event)">
                    <input type="text" placeholder="name" name="name"/> <br/>
                    <input type="text" placeholder="url" name="url"/> <br/>
                    <input type="text" placeholder="width" name="width"/> <br/>
                    <input type="text" placeholder="height" name="height"/> <br/>
                    <input type="text" placeholder="box count" name="boxCount"/>
                    <button type="submit" class="postSubmit">Submit</button>
                  </form>
            </div>
        );
      }
}


class SlideShow extends React.Component {
   constructor(props){
       super(props);
       this.state = {
           pictures: [],
           currentIndex: 0,
        };
   }
   componentDidMount() {
        fetch('/images')
            .then(res => {
                console.log(res);
                return res.json()
             })
            .then(images => { 
                console.log(images);
                this.setState({ 
                    pictures: images.images,  // image array is wrapped in image json
                    currentIndex: 0 
                })
             });
    }

    onClickNext() {
        const nextIndex = this.state.currentIndex + 1;
        if(nextIndex === this.state.pictures.length) {
            this.setState({ 
                pictures: this.state.pictures,
                currentIndex: 0 })    
        } else {
            this.setState({
                pictures: this.state.pictures,
                currentIndex: nextIndex 
            })
        }
    }

    onClickPrevious() {
        const previousIndex = this.state.currentIndex - 1;
        if(previousIndex < 0) {
            this.setState({ 
                pictures: this.state.pictures,
                currentIndex: this.state.pictures.length - 1 })    
        } else {
            this.setState({
                pictures: this.state.pictures,
                currentIndex: previousIndex
            })
        }
    }

    render() {
        const currentIndex = this.state.currentIndex;
        let url;
        if(this.state.pictures.length > 0) {
            url = this.state.pictures[currentIndex].url;
        } else {
            url = ""
        }
        return (
            <div className="main">
                <div className="navigation-buttons">
                  <img src={arrowBack} className="backButton" onClick={() => this.onClickPrevious()}></img>
                  <img src={arrowForward} className="nextButton" onClick={() => this.onClickNext()}></img>
                </div>

                <div><p>current Index: {currentIndex}</p></div>

                <div className="image-wrapper">
                    <img src={url} />
                    <div className="topOut">lol</div>
                    <div className="bottomOut">lol</div>   
                </div>

            </div>
        );
    }
}

// ========================================
class Home extends React.Component {
    render() {
      return (
        <div className="home">
          <div className="input-section">
            <InputSection/>
          </div>
          <div className="slide-show-section">
            <SlideShow/>
          </div>
        </div>
      );
    }
}


ReactDOM.render(
  <Home />,
  document.getElementById('root')
);
