import React from 'react';
import ReactDOM from 'react-dom';
import arrowBack from './img/arrow_back-black-18dp.svg';
import arrowForward from './img/arrow_forward-black-18dp.svg';
import './index.css';


class InputSection extends React.Component {
    render() {
        return (
          <div className="inputs">
            <p>hi</p>
          </div>
        );
      }
}


class SlideShow extends React.Component {
   constructor(props){
       super(props);
       this.state = {
           pictures: [],
           currentIndex: 0,
        };
   }
   componentDidMount() {
        fetch('/images')
            .then(res => {
                console.log(res);
                return res.json()
             })
            .then(images => { 
                console.log(images);
                this.setState({ 
                    pictures: images.images,  // image array is wrapped in image json
                    currentIndex: 0 
                })
             });
    }

    onClickNext() {
        const nextIndex = this.state.currentIndex + 1;
        if(nextIndex === this.state.pictures.length) {
            this.setState({ 
                pictures: this.state.pictures,
                currentIndex: 0 })    
        } else {
            this.setState({
                pictures: this.state.pictures,
                currentIndex: nextIndex 
            })
        }
    }

    onClickPrevious() {
        const previousIndex = this.state.currentIndex - 1;
        if(previousIndex < 0) {
            this.setState({ 
                pictures: this.state.pictures,
                currentIndex: this.state.pictures.length - 1 })    
        } else {
            this.setState({
                pictures: this.state.pictures,
                currentIndex: previousIndex
            })
        }
    }

    render() {
        const currentIndex = this.state.currentIndex;
        let url;
        if(this.state.pictures.length > 0) {
            url = this.state.pictures[currentIndex].url;
        } else {
            url = ""
        }
        return (
            <div className="main">
                <div className="navigation-buttons">
                  <img src={arrowBack} className="backButton" id="backButton" onClick={() => this.onClickPrevious()}></img>
                  <img src={arrowForward} className="nextButton" id="nextButton" onClick={() => this.onClickNext()}></img>
                </div>

                <div><p>current Index: {currentIndex}</p></div>

                <div className="image-wrapper">
                    <div className="image">
                        <img src={url} />
                    </div>
                    <div className="topOut">lol</div>
                    <div className="bottomOut">lol</div>   
                </div>

            </div>
        );
    }
}

// ========================================
class Home extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <InputSection/>
          </div>
          <div className="game-info">
            <SlideShow/>
          </div>
        </div>
      );
    }
}


ReactDOM.render(
  <Home />,
  document.getElementById('root')
);
