import React from 'react';
import ReactDOM from 'react-dom';
import arrowBack from './img/arrow_back-black-18dp.svg';
import arrowForward from './img/arrow_back-black-18dp.svg';
import './index.css';


class Home extends React.Component {
   constructor(){
       super();
       this.state ={users: []};
   }
   componentDidMount() {
          fetch('/users')
            .then(res => {
                console.log(res);
                return res.json()
             })
            .then(users => { 
                console.log(users); 
                this.setState({ users })
             });
         }
   render() {
        return (
            // <div className="App">
            //     <h1>Users</h1>
            //     {this.state.users.map(user =>
            //     <div key={user.id}>user: {user.name} Password: {user.password}</div>
            //   )}
            // </div>
            //
            <div className="main">
                <div className="navigation-buttons">
                  <img src={arrowBack} className="backButton" id="backButton"></img>
                  <img src={arrowForward} className="nextButton" id="nextButton"></img>
                </div>

                <div className="image-wrapper">
                  <figure className="top-figure">
                    <figcaption>
                      <h1 id="topOut"></h1>
                    </figcaption>
                    <div id="slideShowImages">
                      <div className="imageNumber"></div>
                      <p>Loading, please wait...</p>
                    </div>
                    <figcaption>
                      <h2 id="bottomOut"></h2>
                    </figcaption>
                  </figure> 
                </div>

            </div>
        );
    }
}

  // ========================================

  ReactDOM.render(
    <Home />,
    document.getElementById('root')
  );
